  // loop through first array
  for (let i = 0; i < arr1.length; i++) {
    // using the indexOf method to see if each value in arr1 is in array2
    if (arr2.indexOf(arr1[i]) >= 0) {
      // If there is a match, do nothing
    } 
      // if not add this value to the newArr
      else {
      newArr.push(arr1[i]);
    }
  }
  console.log(newArr);

  // Do this in reverse as there may be something on arr2 not on arr1 that will not show up in first filter

  // loop through second array
  for (let i = 0; i < arr2.length; i++) {
    // using the indexOf method to see if each value in arr2 is in array1
    if (arr1.indexOf(arr2[i]) >= 0) {
