function sumAll(arr) {
  // double check to sort the two arguments greatest to least
  function sortNumber(a,b) {
    return a - b;
  }
  let step1 = arr.sort(sortNumber)
  // console.log(step1);
  // save sum of two numbers in storage
  let step2 = arr[0] + arr[1];
  // console.log(step2);
  // take highest value minus lowest value, take N many steps in a loop above lowest character and get sum of all characters
  // console.log(arr[1] - arr[0])
  let step3 = 0;
  for (let i = 1; i < arr[1] - arr[0]; i++) {
    step3 += arr[0] + i;
    // console.log(arr[0] + i)
  }
  // console.log(step3)
  // add these two variables together and return
  return step3 + step2;
}


